mapper.select:
SELECT * FROM CATEGORY
WHERE CATEGORYNUM in(SELECT CATEGORYNUM FROM CATEGORYTYPE c WHERE
machinenum=#{machineNum}
)

SELECT DISTINCT OPTNAME1,OPTID1 FROM REALHEALTHMACHINE
WHERE MACHINENUM = #{machineNum}
order by optname1

 		SELECT COUNT(NUM)
		FROM MACHINEREVIEW
		WHERE MACHINENUM=#{machineNum}

 		SELECT COUNT(NUM)
		FROM MACHINEQNA
		WHERE MACHINENUM=#{machineNum}
